import React, { useState } from 'react'
import styled from 'styled-components'
import Tab from '../Tab/Tab'

const StyledTabs = styled.div`
    width: 100%;
`

const Tabs = ({tabIndex, tabData}) => {
   



    return (
        <StyledTabs>
            {tabData.map((tab, i) => {
                const {label, bg} = tab;
    
                    return (
                        <Tab
                        tabData={tabData}
                        key={i}
                        itemIndex={i}
                        label={label}
                        bg={bg}
                        tabIndex={tabIndex}
                        />
                    )               
               
            })
            }
           
        </StyledTabs>
    )
}

export default Tabs
