import { useState } from 'react';
import './App.css';
import Tab from './components/Tab/Tab';
import TabPanel from './components/TabPanel/TabPanel';
import Tabs from './components/Tabs/Tabs';



function App() {
  const [tabIndex, settabIndex] = useState(0);

  const tabsData = [
    {label: 'content1', bg: '#659DB4'},
    {label: 'content2', bg: '#9992FF'},
    {label: 'content3',  bg: '#254284'}
  ]

  const handleChange = (index) => {
      settabIndex(index);

  }
  return (
    <>
      <TabPanel tabIndex={tabIndex} handleChange={handleChange}/>
      <Tabs tabIndex={tabIndex} renderTab={(info, itemIndex, label, bg) => <Tab info={info} itemIndex={itemIndex} label={label} bg={bg}/>  }/>
    </>
  );
}

export default App;
