import React, { useState } from 'react'
import styled, { css } from 'styled-components'


export const StyledTab = styled.div`
    margin: 0 15px;
    padding: 10px 0;
    color: #000; 
    cursor: pointer;
    position: relative;
    transition: 0.35s;
    ${(p) => p.value === p.itemIndex && css`
      &::before {
        content: '';
        position: absolute;
        width: 100%;
        bottom: 0;
        left: 0;
        transition: 0.35s;
        border-bottom: 2px solid purple;
      }       
    `}

    
`

const Tab = ({label, value, bg, handleChange}) => {

 

    return (
      <StyledTab className='tut' value={value}  bg={bg} onClick={handleChange(value)}>
       {label}
      </ StyledTab>
    )
}

export default Tab
