import React, { useEffect, useRef, useState } from 'react'
import styled from 'styled-components'
import Tab from '../Tab/Tab'



const StyledTabs = styled.div`
    position:fixed;
    top: 0;
    left: 0;
    z-index: 5;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #cecece; 
   
`

const StyledTabsContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    position:relative;
`

const TabSlider = styled.div`
    position: absolute;
    bottom: 0;
    left: ${(p) => `${p.left}px`};
    width: ${(p) => `${p.width}px`};
    height: 4px;
    background: #00abd5;
    transition: 0.35s;
    
`


const Tabs = ({handleChange, tabIndex, renderTab, options, value}) => {

    const containerRef = useRef(null);
    
    const [containerWidth, setContainerWidth] = useState(0);
    const [sliderWidth, setSliderWidth] = useState(0);
    const [sliderLeft, setSliderLeft] = useState(0);

    // useEffect((value) => {
    //     setContainerWidth(containerRef.current.getBoundingClientRect().width);
    //     console.log(value, containerRef.current.getBoundingClientRect());

    // }, [containerRef]);
    // useEffect(() => {
    //     console.log(itemRef.current.getBoundingClientRect());
 
    //  }, [itemRef]);

    

    const getSliderInfo = (e) => {
        const parentTarget = e.target.parentElement;
        if( !parentTarget){
            return;
        }
        setSliderWidth(parentTarget.getBoundingClientRect().width);
        console.log(e.target.parentElement);
        // console.log(e.target.getBoundingClientRect().width);
        setSliderLeft((  parentTarget.getBoundingClientRect().left ) - containerRef.current.getBoundingClientRect().left );
    //   console.log(e.target.getBoundingClientRect().left);
    }

    

    // const sliderWidth = containerWidth / options.length;

    return (
      
        <StyledTabs className='panel' >
            <StyledTabsContainer ref={containerRef}>
            {options.map((tab, i) => {  
                return (
                    <div key={i} onClick={handleChange} className='dada' value={value} onClick={(e) => getSliderInfo(e)}>
                        { renderTab(tab) }
                       
                    </div>
             
              
                )
            })
            }
             <TabSlider width={sliderWidth} left={sliderLeft}></TabSlider>  
            
            </StyledTabsContainer>
        </StyledTabs>

        
        
    )
}

export default Tabs
