import { useState } from 'react';
import './App.css';
import Tab from './components/Tab/Tab';
import TabPanel from './components/TabPanel/TabPanel';
import TabPanelItem from './components/TabPanelItem/TabPanelItem';
import Tabs from './components/Tabs/Tabs';



function App() {
  const [value, setValue] = useState('content2');

  const tabsData = [
    {label: 'content1', bg: '#659DB4', value: 'content1'},
    {label: 'content2', bg: '#9992FF', value: 'content2'},
    {label: 'content3',  bg: '#254284', value: 'content3'}
  ]

  const handleChange = (value) => {
    setValue(value);
    console.log(value);
  };

  const renderT = ({label, bg, handleChange, value }) => {
    return <TabPanelItem  bg={bg} handleChange={handleChange} label={label}/>
  }


  return (
    <div>
      <TabPanel handleChange={handleChange} renderTab={renderT} options={tabsData}/>
      {value === 'content2' ? 'tewetet' : ''}
    </div>
  );
}

export default App;
