import React, { useState } from 'react'
import styled from 'styled-components'
import TabPanelItem, { StyledTabPanelItem } from '../Tab/Tab'


const StyledTabs = styled.div`
    position:fixed;
    top: 0;
    left: 0;
    z-index: 5;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #cecece; 
`

const StyledTabsContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`


const Tabs = ({handleChange, tabIndex, renderTab, options, value}) => {

    console.log(children.length);

    return (
      
        <StyledTabs className='panel' >
            <StyledTabsContainer>
            {options.map((tab, i) => {  
                return (
                    <div key={i} onClick={handleChange} className='dada'>
                        { renderTab(tab) }
                    </div>
             
              
                )
            })
            }
            </StyledTabsContainer>
        </StyledTabs>

        
        
    )
}

export default Tabs
