import React, { useState } from 'react'
import styled from 'styled-components'
import TabPanelItem, { StyledTabPanelItem } from '../TabPanelItem/TabPanelItem'


const StyledTabPanel = styled.div`
    position:fixed;
    top: 0;
    left: 0;
    z-index: 5;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    background: #cecece; 
`


const TabPanel = ({handleChange, tabIndex, renderTab, options}) => {

    const tabsData = [
        {label: 'content1', bg: '#659DB4', value: 'content1'},
        {label: 'content2', bg: '#9992FF', value: 'content2'},
        {label: 'content3',  bg: '#254284', value: 'content3'}
      ]

    return (
      
        <StyledTabPanel>
            
            {tabsData.map((tabPanelItem, i) => {
                return (
                    <div key={i} >
                        { renderTab(...tabPanelItem) }
                    </div>
             
              
                )
            })
            }
        </StyledTabPanel>

        
        
    )
}

export default TabPanel
